import React from 'react';
import { Link } from '@reach/router';
import Button from './Button';
import { getTopics } from '../api';

const Topics = () => {
  const { topics } = { "topics": [{ "slug": "coding", "description": "Code is love, code is life" }, { "slug": "football", "description": "FOOTIE!" }, { "slug": "cooking", "description": "Hey good looking, what you got cooking?" }] };
  return <div>
    <h3>Topics</h3>
    <form>
      <input type="text" />
      <input type="submit" value="Add New Topic" />
    </form>
    {topics.map(({ slug }) => {
      return <Link to={`/topics/${slug}`}>{slug}</Link>
    })}
  </div>
};

export default Topics;
