import React, { Component } from 'react';
import CommentAdder from './CommentAdder';
import * as api from '../api';
import Button from './Button';
import { navigate } from '@reach/router';
import IconImage from './IconImage';
import like_icon from '../icons/like_icon.png';
import dislike_icon from '../icons/dislike_icon.png';

//like image url = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQKK0f9xcr-bN85vhDUmKZ_4fuxXRjJdrRN88qEqM67BjwW2nmC5A'
//dislike url = 'https://png.pngtree.com/svg/20151120/d7e27c0b9d.png'

class Article extends Component {
  state = {
    article: {},
    comments: [],
    loading: true
  }
  render() {
    const { username } = this.props;
    const { article, comments, loading } = this.state;
    return <div>
      <h3>{article.title}</h3>
      {loading && 'Loading...'}
      <h4>Created by {article.author}, {article.created_at}</h4>
      <p>{article.body}</p>
      <p>Votes: {article.votes}</p>
      {username === article.author && <Button text='Delete article' handleClick={this.handleClick} />}
      <IconImage src={like_icon} description='like' vote={() => this.handleVoteClick(1)} />
      <IconImage src={dislike_icon} description='dislike' vote={() => this.handleVoteClick(-1)} />
      <h4>{article.comment_count} Comments:</h4>
      <CommentAdder />
      {comments.map(comment => {
        return <div key={comment.comment_id}>
          <p>{comment.author}: {comment.body}</p>
          <IconImage src={like_icon} description='like' />
          <IconImage src={dislike_icon} description='dislike' />
        </div>
      })}
    </div>
  }

  componentDidMount() {
    const { article_id } = this.props;
    console.log(article_id)
    api.getArticle(article_id).then(article => {
      this.setState({ article, loading: false })
    });
    api.getComments(article_id).then(comments => {
      this.setState({ comments })
    })
  }

  handleClick = (event) => {
    const { article_id } = this.props;
    api.deleteArticle(article_id)
      .then(() => {
        navigate('/articles');
      })
  }
}

export default Article;
