import React, { Component } from 'react';
import { Link, Router } from '@reach/router';
import Button from './Button';
import * as api from '../api';

class Articles extends Component {
  state = {
    articles: [],
    total_count: 0,
    loading: true,
    sort_by: 'created_at'
  }
  render() {
    return <div>
      {/* <Router>
        <h2 path="/">Currently trending</h2>
      </Router> */}
      {this.props.path === '/' && <h2>Currently trending</h2>}
      {this.props.path === '/articles' && <h2>All articles</h2>}
      {this.props.path === '/topics/:topic' && <h2>{this.props.topic}</h2>}
      <Link to="/articles/add"><Button to="/articles/add" text="Add new article" /></Link>
      {this.state.loading && 'Loading...'}
      {this.state.articles.map(({ title, author, article_id, votes }) => {
        return <Link key={article_id} to={`/articles/${article_id}`}>
          <h4>{title}</h4>
          <p>{votes}</p>
          <p>{author}</p>
        </Link>
      })}
    </div>
  }

  componentDidMount() {
    const { sort_by } = this.state;
    api.getArticles(sort_by).then(({ articles, total_count }) => {
      this.setState({ articles, total_count, loading: false })
    })
  }
}

export default Articles;
