import React from 'react';
import { Link } from '@reach/router';
import Button from './Button';

const Topics = () => {
  const { topics } = { "topics": [{ "slug": "coding", "description": "Code is love, code is life" }, { "slug": "football", "description": "FOOTIE!" }, { "slug": "cooking", "description": "Hey good looking, what you got cooking?" }] };
  return <div>
    <h3>Topics</h3>
    <Button text="Add New Topic" />
    {topics.map(({ slug }) => {
      <Link to={`/topics/${slug}`}>{slug}</Link>
    })}
    <Link to="/topics/topic1"><p>topic 1</p></Link>
    <Link to="/topics/topic2"><p>topic 2</p></Link>
  </div>
};

export default Topics;
