import React, { Component } from 'react';
import CommentAdder from './CommentAdder';
import * as api from '../api';
import Button from './Button';

class Article extends Component {
  state = {
    article: {},
    comments: [],
    loading: true
  }
  render() {
    const { username } = this.props;
    const { article, comments, loading } = this.state;
    return <div>
      <h3>{article.title}</h3>
      {loading && 'Loading...'}
      <h4>Created by {article.author}, {article.created_at}</h4>
      <p>{article.body}</p>
      {username === article.author && <Button text='Delete article' />}
      <h4>{article.comment_count} Comments:</h4>
      <CommentAdder />
      {comments.map(comment => {
        return <p>{comment.author}: {comment.body}</p>
      })}
    </div>
  }

  componentDidMount() {
    const { article_id } = this.props;
    api.getArticle(article_id).then(article => {
      this.setState({ article, loading: false })
    });
    api.getComments(article_id).then(comments => {
      this.setState({ comments })
    })
  }
}

export default Article;
