import React, { Component } from 'react';
import { Link } from '@reach/router';
import Button from './Button';
import * as api from '../api';

// const articles = api.getArticles()

class Articles extends Component {
  state = {
    articles: [],
    total_count: 0
  }
  render() {
    return <div>
      {this.props.path === '/' && <h2>Currently trending</h2>}
      {this.props.path === '/articles' && <h2>All articles</h2>}
      {this.props.path === '/topics/:topic' && <h2>{this.props.topic}</h2>}
      <Link to="/articles/add"><Button to="/articles/add" text="Add new article" /></Link>
      {this.state.articles.map(({ title, author, article_id }) => {
        return <Link to={`/articles/${article_id}`}>
          <h4>{title}</h4>
          <p>{author}</p>
        </Link>
      })}
    </div>
  }
}

export default Articles;
